import React from 'react';
import { Jumbotron, Button } from 'reactstrap';
import CategoryCarousel from './CategoryCarousel';

 

class CategoryJumbo extends React.Component{


    state = {
        searchString : this.props.searchString,
        data: {},
        gotData: false
    } 

    async componentDidMount(){
        const key = "be04037ec49e466087cc0901fb6ba5ec";
        console.log(this.state.searchString);
            const response = await fetch(`https://newsapi.org/v2/everything?q=${this.state.searchString}&apiKey=${key}`);
            const json = await response.json();
            this.setState({
                data: json.articles,
                gotData: true,
            });
    }

    render(){

        return(
        <div>
            <Jumbotron>
              <h1 className="display-3">Hello, world!</h1>
              <p className="lead">This is a simple hero unit, a simple Jumbotron-style component for calling extra attention to featured content or information.</p>
              <hr className="my-2" />
              <p>It uses utility classes for typography and spacing to space content out within the larger container.</p>
              <p className="lead">
                <Button color="primary">Learn More</Button>
              </p>
            </Jumbotron>
            {console.log('is this an array' + this.state.data)}
            {/* <CategoryCarousel data={this.state.data} /> */}
        </div>
            );
    }
}

export default CategoryJumbo;
