import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, Input, Label, FormGroup } from 'reactstrap';
import SearchResults from './SearchResults';
import { Redirect } from 'react-router-dom';

class SearchButton extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      modal: false,
      name: '',
      search: false
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value});
    }

  onSubmit = (e) => {
    e.preventDefault();

    this.setState(prevState => ({
        modal: !prevState.modal
    }));

    console.log(this.state.name);

    this.setState({search: true});
  }

  render(){
      if(this.state.search) {
        return <SearchResults searchString = {this.state.name}/>
      }

    return (
      <div>
        <Button color="danger" onClick={this.toggle}>Search</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Search</ModalHeader>
          <ModalBody>
              Search for a topic 
          </ModalBody>
          <Form
                            onSubmit={this.onSubmit}>
                            <FormGroup>
                                <Input
                                    type="text"
                                    name= "name"
                                    onChange = {this.onChange}
                                />
                                <Button color="primary" onClick={this.onSubmit}>Submit</Button>{' '}
                            </FormGroup>
            </Form>
          <ModalFooter>
            
          </ModalFooter>
        </Modal>

 
      </div>
    );
  }
}

export default SearchButton;
