import React from 'react';

class Thread extends React.Component{


    constructor(props) {
        super(props);
        this.state = {
            data: {},
            gotData: false,
            inputText: ''
        };
    }

    async GetReddit(subreddit) {
        const response = await fetch(`https://www.reddit.com/${subreddit}`);
        const json = await response.json();
        console.log(json);
        this.setState({
            data: json,
            gotData: true
    });
    }

    render(){
        return(
            <div>
                <p>{this.state.subreddit}</p>
            <p>thread</p>
            
            </div>

        );
    }


}

export default Thread;
