import React, { Component } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption, Col
} from 'reactstrap';
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, Row } from 'reactstrap';



class StoryCard extends Component {
  constructor(props) {
    super(props);
    this.state = { activeIndex: 0 ,
        items : props.data
        
    };
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.goToIndex = this.goToIndex.bind(this);
    this.onExiting = this.onExiting.bind(this);
    this.onExited = this.onExited.bind(this);
  }

  onExiting() {
    this.animating = true;
  }

  onExited() {
    this.animating = false;
  }

  next() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === this.state.items.length - 1 ? 0 : this.state.activeIndex + 1;
    this.setState({ activeIndex: nextIndex });
  }

  previous() {
    if (this.animating) return;
    const nextIndex = this.state.activeIndex === 0 ? this.state.items.length - 1 : this.state.activeIndex - 1;
    this.setState({ activeIndex: nextIndex });
  }

  goToIndex(newIndex) {
    if (this.animating) return;
    this.setState({ activeIndex: newIndex });
  }

  render() {
    const { activeIndex } = this.state;

    const imgStyle = {
        maxHeight: 300,
        maxWidth: 300
      }

      var zero = this.state.items.pop();
      var one = this.state.items.pop();
      var two = this.state.items.pop();
      var three = this.state.items.pop();
      var four = this.state.items.pop();
      var five = this.state.items.pop();
      var six = this.state.items.pop();
      var seven = this.state.items.pop();
      var eight = this.state.items.pop();

    const slides = 
        
        <CarouselItem
          onExiting={this.onExiting}
          onExited={this.onExited}
          key= {1}
        >
        <Row>
      <Col sm="4">
          
        <Card body>
          <CardTitle>{}</CardTitle>
          <CardText>{console.log(zero)}</CardText>
          <Button>Go somewhere</Button>
        </Card>

      </Col>
      <Col sm="4">
        <Card body>
          <CardTitle>Special Title Treatment</CardTitle>
          <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
          <Button>Go somewhere</Button>
        </Card>
      </Col>
      <Col sm="4">
        <Card body>
          <CardTitle>Special Title Treatment</CardTitle>
          <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
          <Button>Go somewhere</Button>
        </Card>
      </Col>
    </Row>
        </CarouselItem>
       

    console.log(this.state.items);

    return (
      <Carousel
        activeIndex={activeIndex}
        next={this.next}
        previous={this.previous}
      >
        {/* <CarouselIndicators items={this.state.items} activeIndex={activeIndex} onClickHandler={this.goToIndex} /> */}
        {slides}
        <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
      </Carousel>
    );
  }
}


export default StoryCard;
