    return(
        <div>
            <Nav />
            {
                        this.state.gotData && this.state.data.data.children.map((child, index) => {
                            const { data } = child;
                            return (
                                <Thread
                                    key={data.id}
                                    index={index + 1}
                                    title={data.title}
                                    points={data.score}
                                    comments={data.num_comments}
                                    subreddit={data.subreddit_name_prefixed}
                                    user={data.author}
                                    data={data}
                                />
                            );
                        })
                    }
        </div>
    );
}


}

export default CategoryPage;
