import { GET_HEADLINES, ADD_HEADLINE, DELETE_HEADLINE, HEADLINES_LOADING} from './types';
import axios from 'axios';

export const getHeadlines = () => dispatch => {
    dispatch(setHeadlinesLoading());
    axios.get('/api/headlines')
    .then(res => 
        dispatch({
            type: GET_HEADLINES,
            payload: res.data
        }) 
    )
;}

export const addHeadline = (headline) => dispatch => {
   axios
    .post('/api/headlines', headline)
    .then(res => 
        dispatch({
            type: ADD_HEADLINE,
            payload: res.data
        })
    )
;}


export const deleteHeadline = (id) => dispatch => {
    axios.delete(`/api/headlines/${id}`).then( res =>
        dispatch({
            type:DELETE_HEADLINE,
            payload: id
        })
        
    );
;}


export const setHeadlinesLoading = () => {
    return {
        type: HEADLINES_LOADING
    }
};
