//                     ? (<img src={data.thumbnail}></img>) 
//                     : (<img style={styles.thumbSize} src="https://b.thumbs.redditmedia.com/en9Scp_T9_RTkVsX_YNUqZOyj-O5V63wIAQ-TRKdsFA.png"></img>)
//                 }
//             </td>
//             <td>
//                 <a target="_blank" href={`https://reddit.com${data.permalink}`}>
//                     <p>{props.title}</p>
//                     <p>{`${props.points} points - ${props.comments} comments - ${props.subreddit} - Posted by ${props.user}`}</p>
//                 </a>
//             </td>
//         </tr>
//         </table>
//     );
// }

class Thread extends React.Component{
    state = {
        styles : {
            titleStyle: {
                color: 'red',
            },
            thumbSize: {
                width: 140,
                height: 105
            },
        },

        data : this.props

    }

    render(){
        return (
            <div>
            <div>
      <Jumbotron>
        <h1 className="display-3">{this.props.index}</h1>
        <p className="lead">This is a simple hero unit, a simple Jumbotron-style component for calling extra attention to featured content or information.</p>
        <hr className="my-2" />
        <p>It uses utility classes for typography and spacing to space content out within the larger container.</p>
        <p className="lead">
          <Button color="primary">Learn More</Button>
        </p>
      </Jumbotron>
    </div>
            <table>
            <tr>
                <td style={this.state.styles.titleStyle}>{this.props.index}</td>
                <td>
                    {
                        (this.props.data.thumbnail !== "self") 
                        ? (<img src={this.props.data.thumbnail}></img>) 
                        : (<img style={this.props.styles.thumbSize} src="https://b.thumbs.redditmedia.com/en9Scp_T9_RTkVsX_YNUqZOyj-O5V63wIAQ-TRKdsFA.png"></img>)
                    }
                </td>
                <td>
                    <a target="_blank" href={`https://reddit.com${this.props.data.permalink}`}>
                        <p>{this.props.title}</p>
                        <p>{`${this.props.points} points - ${this.props.comments} comments - ${this.props.subreddit} - Posted by ${this.props.user}`}</p>
                    </a>
                </td>
            </tr>
            </table>
            </div>
        );
    }
}


export default Thread;
