import React from 'react';
import { Card, CardImg, CardText, CardBody, CardLink,
  CardTitle, CardSubtitle } from 'reactstrap';
import { getAds, deleteAd } from './../../actions/adActions';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class AdBox extends React.Component{

    state={
        singleAd : [null ]
    }

    componentDidMount(){
        this.props.getAds();
       
        
        
    }


    render(){
        const { ads }  = this.props.ad;

        // ads.map((item,index) =>{
            
        //     if(index < 1){
        //         this.state.singleAd.push(ads.pop())}
        //     else{}
        // })

        

      var ad = ads[0];
      console.log(ad.json());

  return (
    <div>
      <Card>
        <CardBody>
          <CardTitle>ad.headline</CardTitle>
          <CardSubtitle>Card subtitle</CardSubtitle>
        </CardBody>
        <img width="100%" src="https://placeholdit.imgix.net/~text?txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap" />
        <CardBody>
          <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
          <CardLink href="#">Card Link</CardLink>
          <CardLink href="#">Another Link</CardLink>
        </CardBody>
      </Card>
    </div>
  );}
}

AdBox.propTypes = {
    getAds: PropTypes.func.isRequired,
    ad: PropTypes.object.isRequired,
    isAuthenticated: PropTypes.bool
}

const mapStateToProps = (state) => ({
    ad: state.ad,
    isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { getAds, deleteAd })(AdBox);
