import React from 'react';
import {Jumbotron, Button, Container, Row, Col} from 'reactstrap';

class Thread extends React.Component{
    state = {
        styles : {
            titleStyle: {
                color: 'red',
            },
            thumbSize: {
                width: 140,
                height: 105
            },
        },

        data : this.props

    }

    render(){
        return (
            <div>
      <Jumbotron style={{"background" : "linear-gradient(0deg, rgba(110,157,163,0.23015143557422968) 0%, rgba(176,192,194,0.19373686974789917) 100%, rgba(209,209,209,0.8491990546218487) 100%, rgba(144,63,1,0.8604035364145658) 100%)"}}>
          <Container>
              <Row>
                  <Col xs="12" sm="3" md="3" lg="3"  className = "headline-thumbnail">{
                        (this.props.data.thumbnail === "default" || this.props.data.thumbnail === "self" || this.props.data.thumbnail === "nsfw" || this.props.data.thumbnail === "") 
                        ? (<img height="80" width="80" src={this.props.img} alt="thumbnail" ></img>)
                        : (<img src={this.props.data.thumbnail} alt="thumbnail"></img>) 
                    }</Col>
                    <Col xs="12" ><h3><a className="link" href={this.props.data.url}>{this.props.title}</a></h3></Col>
              </Row>
          </Container>

          <p className="lead">
          <Button outline color="info" href={this.props.data.url}>Learn More</Button>
        </p>
      </Jumbotron>

            </div>
        );
    }
}


export default Thread;
