import { GET_UPDATES, ADD_UPDATE, DELETE_UPDATE, UPDATES_LOADING} from './types';
import axios from 'axios';

export const getUpdates = () => dispatch => {
    dispatch(setUpdatesLoading());
    axios.get('/api/updates')
    .then(res => 
        dispatch({
            type: GET_UPDATES,
            payload: res.data
        }) 
    )
;}

export const addUpdate = (update) => dispatch => {
   axios
    .post('/api/updates', update)
    .then(res => 
        dispatch({
            type: ADD_UPDATE,
            payload: res.data
        })
    )
;}


export const deleteUpdate = (id) => dispatch => {
    axios.delete(`/api/updates/${id}`).then( res =>
        dispatch({
            type:DELETE_UPDATE,
            payload: id
        })
        
    );
;}


export const setUpdatesLoading = () => {
    return {
        type: UPDATES_LOADING
    }
};
