import React from 'react';
import { Jumbotron, Button } from 'reactstrap';



class CategoryJumbo extends React.Component{



    state = {
        searchString : this.props.searchString,
        data: {},
        upData: [],
        gotData: false
    } 

    async componentDidMount(){
        const key = "be04037ec49e466087cc0901fb6ba5ec";
            const response = await fetch(`https://newsapi.org/v2/everything?q=${this.state.searchString}&apiKey=${key}`);
            const json = await response.json();
            this.setState({
                data: json.articles,
                gotData: true,
            });

            this.state.data.map((item, index) => {
                if(index < 4){this.state.upData.push(item)}
            });
        console.log(this.state.upData);
    }

    render(){

        return(
        <div>
            {this.state.upData.map((item) => (
                
            <Jumbotron backgroundImage={item.urlToImage}>
              <h6 className="display-4">{item.title}</h6>
              <p className="lead">{item.description}</p>
              <hr className="my-2" />
              <p>{item.description}</p>
              <p className="lead">
                <Button color="primary">Learn More</Button>
              </p>
            </Jumbotron>
            ))}
            {console.log(this.state.data)}
        </div>
            );
    }
}

export default CategoryJumbo;
