import { GET_GREETINGS, ADD_GREETING, DELETE_GREETING, GREETINGS_LOADING} from './types';
import axios from 'axios';

export const getGreetings = () => dispatch => {
    dispatch(setGreetingsLoading());
    axios.get('/api/greetings')
    .then(res => 
        dispatch({
            type: GET_GREETINGS,
            payload: res.data
        }) 
    )
;}

export const addGreeting = (greeting) => dispatch => {
   axios
    .post('/api/greetings', greeting)
    .then(res => 
        dispatch({
            type: ADD_GREETING,
            payload: res.data
        })
    )
;}


export const deleteGreeting = (id) => dispatch => {
    axios.delete(`/api/greetings/${id}`).then( res =>
        dispatch({
            type:DELETE_GREETING,
            payload: id
        })
        
    );
;}


export const setGreetingsLoading = () => {
    return {
        type: GREETINGS_LOADING
    }
};
