      </Media>
      <Media body>
        <Media heading>
          {this.props.title}
        </Media>
            {this.props.data.description}
            <p>Date Published:  {realTime} </p>
        </Media>
    </Media>
        
            </div>
        );
    }
}


export default Thread;
