import React from 'react';
import {Jumbotron, Button, Media} from 'reactstrap';

class Thread extends React.Component{
    state = {
        styles : {
            titleStyle: {
                color: 'red',
            },
            thumbSize: {
                width: 140,
                height: 105
            },
        },

        data : this.props

    }

    render(){
        return (
            <div>
      <Jumbotron>
          <table>
              <tr>
              <td className = "headline-thumbnail">{
                        (this.props.data.thumbnail !== "default") 
                        ? (<img src={this.props.data.thumbnail}></img>) 
                        : (<img height="100" width="100" src={this.props.img}></img>)
                    }</td>
      <td><h3><a className="link" href={this.props.data.url}>{this.props.title}</a></h3></td>
      
      </tr>
      </table>
        <p className="lead">
          <Button outline color="success" href={this.props.data.url}>Learn More</Button>
        </p>
      </Jumbotron>

            </div>
        );
    }
}


export default Thread;
