import { GET_ADS, ADD_AD, DELETE_AD, ADS_LOADING} from './types';
import axios from 'axios';

export const getAds = () => dispatch => {
    dispatch(setAdsLoading());
    axios.get('/api/featuredAds')
    .then(res => 
        dispatch({
            type: GET_ADS,
            payload: res.data
        }) 
    )
;}

export const addAd = (ad) => dispatch => {
   axios
    .post('/api/featuredAds', ad)
    .then(res => 
        dispatch({
            type: ADD_AD,
            payload: res.data
        })
    )
;}


export const deleteAd = (id) => dispatch => {
    axios.delete(`/api/featuredAds/${id}`).then( res =>
        dispatch({
            type:DELETE_AD,
            payload: id
        })
        
    );
;}


export const setAdsLoading = () => {
    return {
        type: ADS_LOADING
    }
};
