import { GET_GREETINGS, ADD_GREETING, DELETE_GREETING} from './../actions/types';
 
 
const initialState = {
    greetings: [
        { name: 'hi',
            id: 1
    
        },
        { name : 'yoo',
        id: 2
    
        }
    ]

}


 export default function (state = initialState, action) {
    switch(action.type){
        case GET_GREETINGS:
            return {
                ...state
            };
        case DELETE_GREETING:
            return{
                ...state,
                greetings: state.greetings.filter(greeting => greeting.id !== action.payload)
            };
        case ADD_GREETING:
            return{
                ...state,
                greetings: [action.payload,...state.greetings]

            };

        default:
            return state;

    }
 }
